// env-template.js - Environment configuration templates

export function createEnvTemplate(profile = 'default') {
  const baseTemplate = `# Claude-Flow Environment Configuration
# Generated by: claude-flow init

# Node Environment
NODE_ENV=development

# Claude-Flow Settings
CLAUDE_FLOW_LOG_LEVEL=info
CLAUDE_FLOW_MAX_AGENTS=20
CLAUDE_FLOW_MEMORY_SIZE=2GB
`;

  const neo4jTemplate = `
# Cognitive Triangulation Configuration
# Set to true to use real Neo4j + Redis (requires docker-compose up -d)
USE_REAL_COGNITIVE_TRIANGULATION=false

# Neo4j Configuration (Required for real Cognitive Triangulation)
NEO4J_URI=bolt://localhost:7687
NEO4J_USER=neo4j
NEO4J_PASSWORD=test1234
NEO4J_DATABASE=neo4j

# Redis Configuration (Required for real Cognitive Triangulation)
REDIS_HOST=localhost
REDIS_PORT=6379
`;

  const advancedTemplate = `
# Advanced Configuration (Optional)
# CLAUDE_API_KEY=your-claude-api-key
# OPENAI_API_KEY=your-openai-api-key
# DEEPSEEK_API_KEY=your-deepseek-api-key

# MCP Tool Settings
MCP_TOOLS_ENABLED=true
MCP_PARALLEL_EXECUTION=true
MCP_TOOL_TIMEOUT=300

# Performance Settings (for 16GB MacBook)
MAX_CONCURRENT_OPERATIONS=50
BATCH_SIZE=200
WORKER_THREADS=8
CACHE_SIZE=1GB
`;

  if (profile === 'full' || profile === 'sparc') {
    return baseTemplate + neo4jTemplate + advancedTemplate;
  }
  
  return baseTemplate;
}

export function createEnvExampleTemplate() {
  return `# Example environment configuration
# Copy this to .env and update with your values

# Required Settings
NODE_ENV=development

# Optional: Neo4j for Cognitive Triangulation
# To use real knowledge graphs, install Neo4j and configure:
# 1. Install Neo4j Desktop or use Docker:
#    docker run -p 7687:7687 -e NEO4J_AUTH=neo4j/password neo4j
# 2. Uncomment and update these settings:
# NEO4J_URI=bolt://localhost:7687
# NEO4J_USER=neo4j
# NEO4J_PASSWORD=your-password

# Optional: AI Service Keys
# Add if you want to use specific AI providers
# CLAUDE_API_KEY=sk-...
# OPENAI_API_KEY=sk-...

# Performance Tuning
# Adjust based on your system specs
MAX_AGENTS=10
MEMORY_SIZE=1GB
PARALLEL_EXECUTION=true
`;
}